public static String extractDomainName(String base_url) throws URISyntaxException {
    URI siteURI =
            new URI(base_url);
    base_url = siteURI.getHost();
    base_url = base_url.startsWith("www.") ? base_url.substring(4) : base_url;

    String pattern_exceptDot = "[^.]*";
    String pattern_base = pattern_exceptDot + "\\." + pattern_exceptDot;
    String patternCurrent = pattern_base;

    String base_url_temp = base_url;
    Pattern pattern = Pattern.compile("(" + pattern_base + ")$");
    Matcher matcher = pattern.matcher(base_url);
    if (matcher.find())
        base_url_temp = matcher.group(0);
    boolean shouldLoopTLd = true;
    while (shouldLoopTLd)
        for (String tld : Utility.TLDList()) {
            shouldLoopTLd = false;
            if (base_url_temp.toLowerCase().startsWith(tld.toLowerCase() + ".")) {
                shouldLoopTLd = true;
                patternCurrent = pattern_exceptDot + "\\." + patternCurrent;
                pattern = Pattern.compile("(" + patternCurrent + ")$");
                matcher = pattern.matcher(base_url);
                matcher = matcher.reset();
                if (matcher.find())
                    base_url_temp = matcher.group(0);
                break;
            }
        }
    base_url = base_url_temp;
    return base_url;
}

